class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        pref = [[0] for _ in range(31)]
        for i in range(n):
            for b in range(31):
                pref[b].append(pref[b][-1] + ((nums[i] >> b) & 1))
        def get_larger(limit):
            l = 0
            curr = -1
            ans = 0
            for r in range(n):
                curr &= nums[r]
                while curr < limit:
                    curr = 0
                    for b in range(31):
                        if pref[b][r + 1] - pref[b][l + 1] == r - l:
                            curr |= (1 << b)
                    l += 1
                    if l > r:
                        curr = -1
                        break
                ans += (r - l + 1)
            return ans
        return get_larger(k) - get_larger(k + 1)
