class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long,long long>mp;
        long long cnt=0;

        //mp[xr]++;

        for(int i=0;i<n;i++){
            unordered_map<long long,long long>mpp;
            if(nums[i]==k)cnt++;
            mpp[nums[i]]=1;
            for(auto&[val,freq]:mp){
                long long cnt2=val&nums[i];
                if(cnt2==k)cnt+=freq;
                mpp[cnt2]+=freq;
            }
            mp=mpp;
            // xr=xr^nums[i];
            // int des=xr^k;
            // cnt+=mp[des];
            // mp[xr]++;
        }
        return cnt;
    }
};
