class Solution
{
public:
    long long countSubarrays(vector<int> &nums, int k)
    {
        vector<pair<int, int>> v;
        long long res = 0;
        const int n = nums.size();
        for (int i = 0; i < n; i++)
        {
            vector<pair<int, int>> v1;
            for (auto it : v)
            {
                if (v1.empty() || (it.first & nums[i]) != v1.back().first)
                {
                    v1.emplace_back(it.first & nums[i], it.second);
                }
                else
                    v1.back().second += it.second;
            }
            v.swap(v1);
            v.emplace_back(nums[i], 1);
            for (auto it : v)
            {
                if (it.first == k)
                    res += it.second;
            }
        }
        return res;
    }
};
