public class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long cnt = 0; 
        HashMap<Long, Long> prev = new HashMap<>();
        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> cur = new HashMap<>();
            if (nums[i] == k) {
                ++cnt;
            }
            cur.put((long) nums[i], 1L);
            for (HashMap.Entry<Long, Long> entr : prev.entrySet()) {
                long val = entr.getKey();
                long f = entr.getValue();
                long res = val & nums[i];
                if (res == k) {
                    cnt += f;
                }
                cur.put(res, cur.getOrDefault(res, 0L) + f);
            }
            prev = cur;
        }
        return cnt;
    }
}
