public class Solution {
    public long countSubarrays(int[] arr, int k) {
        int n = arr.length;
        long ans = 0; 
        HashMap<Long, Long> map = new HashMap<>();
        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> mapcurr = new HashMap<>();
            if (arr[i] == k) {
                ++ans;
            }
            mapcurr.put((long) arr[i], 1L);
            for (HashMap.Entry<Long, Long> entr : map.entrySet()) {
                long val = entr.getKey();
                long f = entr.getValue();
                long res = val & arr[i];
                if (res == k) {
                    ans += f;
                }
                mapcurr.put(res, mapcurr.getOrDefault(res, 0L) + f);
            }
            map = mapcurr;
        }
        return ans;
        }
}
