class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        dic={}
        ans=0

        for i in nums:
            dic0={}
            for j in dic:
                temp=j&i
                if temp==k:
                    ans+=dic[j]

                if temp in dic0:
                    dic0[temp]+=dic[j]
                else:
                    dic0[temp]=dic[j]
            if i==k:
                ans+=1
            if i in dic0:
                dic0[i]+=1
            else:
                dic0[i]=1
            dic=dic0
        return ans
