class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long result = 0;
    vector<unordered_map<long long, long long>> accumulator(n);

    for (int i = 0; i < n; ++i) {
        if (nums[i] == k) {
            result++;
        }
        accumulator[i][nums[i]] = 1;

        if (i > 0) {
            for (const auto& [key, count] : accumulator[i-1]) {
                long long newKey = key & nums[i];
                if (newKey == k) {
                    result += count;
                }
                accumulator[i][newKey] += count;
            }
        }
    }

    return result;

    }
};
