class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        a = []
        i0 = -1
        for i, x in enumerate(nums):
            if (k & x) != k:
                a = []
                i0 = i
                continue
            a.append([x, i])
            j = 0
            for p in a:
                p[0] = p[0] & x
                if a[j][0] != p[0]:
                    j += 1
                    a[j] = p
                else:
                    a[j][1] = p[1]
            del a[j + 1:]
            if a[0][0] == k:
                ans += a[0][1] - i0
        return ans
