class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
  unordered_map<long long, long long>prev;
  long long res = 0;
  for (long long i = 0; i < n; ++i)
  {
    unordered_map<long long, long long>curr;
    if (nums[i] == k)res++;
    curr[nums[i]] = 1;
    for (auto it : prev) {
      long long temp = it.first & nums[i];
      if (temp == k) {
        res += it.second;
      }
      curr[temp] += it.second;
    }
    prev = curr;
  }
        return res;

    }
};
