#define ll long long int
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ans=0;
        unordered_map<ll,ll>mp;
        for(ll i=0;i<n;i++){
            unordered_map<ll,ll>mp2;
            if(k==nums[i]){
                ans++;
            }
            mp2[nums[i]]=1;
            for(auto& [it,it2]:mp){
                ll andd=it & nums[i];
                if(k==andd){
                    ans+=it2;
                }
                mp2[andd]+=it2;
            }
            mp=mp2;
        }
        return ans;
    }
};
