class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0, val;
        unordered_map<long long, long long> frequency;
        for (int num : nums) {
            unordered_map<long long, long long> temp;
            if (num == k) {
                ans++;
            }
            temp[num]++;
            for (auto it : frequency) {
                val = it.first & num;
                if (val == k) {
                    ans += it.second;
                }
                temp[val] += it.second;
            }
            frequency = temp;
        }
        return ans;
    }
};
