class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long,long long> mp;
        for(auto it:nums){
            ans+=(long long)(it==k?1:0);
            unordered_map<long long,long long> mp1;
            mp1[it]=1;
            for(auto it1:mp){
                long long temp = it1.first&it;
                ans+=(long long)(temp==k?it1.second:0);
                mp1[temp]+=it1.second;
            }
            mp=mp1;
        }
        return ans;
    }
};
