class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        std::map<int, int> dp;
        for (auto i{0}; i < n; ++i) {
            // 以nums[i]结尾的
            std::map<int, int> pre;
            std::swap(pre, dp);
            ++dp[nums[i]];
            if (nums[i] == k) ans += 1;
            for (auto it = pre.rbegin(); it != pre.rend(); ++it) {
                auto [x, cnt] = *it;
                int y = x & nums[i];
                if (y < k) break;
                if (y == k) ans += cnt;
                dp[y] += cnt;
            }
        }
        return ans;
    }
};
