class Solution {
public:
    #define ll long long
    long long pref[(int)(1e5+5)][33];
    ll range(ll l, ll r){
        ll ans=0;
        for(int i=0;i<=32;i++){
            if((pref[r+1][i]-pref[l][i])==(r-l+1)){
                ans += 1ll<<i;
            }
        }
        return ans;
    }

    ll lower(ll ind, ll k, ll n){
        ll s=ind, e=n-1;
        while(s<=e){
            ll mid = (s+e)/2;
            if(range(ind,mid)<=k){
                e = mid - 1;
            }
            else{
                s = mid + 1;
            }
        }
        return s;
    }

    ll upper(ll ind, ll k, ll n){
        ll s=ind, e=n-1;
        while(s<=e){
            ll mid = (s+e)/2;
            if(range(ind,mid)<k){
                e = mid - 1;
            }
            else{
                s = mid + 1;
            }
        }
        return s;
    }

    long long countSubarrays(vector<int>& a, int k) {
        memset(pref,0,sizeof(pref));
        int n=a.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<=32;j++){
                if((ll)a[i]&(1ll<<j)){
                    pref[i+1][j]++;
                }
            }
            for(int j=0;j<=32;j++){
                pref[i+1][j]+=pref[i][j];
            }
        }
        ll ans=0;
        for(int i=0;i<n;i++){
            ans += ( upper(i, k, n) - lower(i, k, n) ); 
        }
        return ans;
    }
};
