class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
    long long count = 0;
    unordered_map<long long, long long> prefixCount;

    // Iterate through the array
    for (int i = 0; i < n; ++i) {
        long long currentAND = nums[i];
        if (currentAND == k) {
            count++;
        }
        unordered_map<long long, long long> newPrefixCount;
        newPrefixCount[currentAND]++;

        for (auto& entry : prefixCount) {
            long long newAND = entry.first & currentAND;
            newPrefixCount[newAND] += entry.second;
            if (newAND == k) {
                count += entry.second;
            }
        }

        prefixCount = newPrefixCount;
    }

    return count;
    }
};
