class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size();
        map<ll,ll>mp;
        ll ct=0;
        for(auto i: nums){
            map<ll,ll>mp1;
            mp1[i]++;
            if(i==k)ct++;
            for(auto j: mp){
                mp1[j.first&i]+=j.second;
                if((j.first&i)==k)ct+=j.second;
            }
            // for(auto i: mp1){
            //     cout<<i.first<<" "<<i.second<<endl;
            // }
            // cout<<endl;
            mp=mp1;
        }
        return ct;
    }
};
