class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int idx[30];
        multiset<int> st;
        for (int i = 0; i < 30; ++i) {
            idx[i] = -1;
        }
        int cnt = 0;
        for (int i = 0; i < 30; ++i) {
            if ((k & (1 << i)) == 0)
                ++cnt;
        }
        int l = -1;
        long long ans = 0;
        for (int i = 0; i < v.size(); ++i) {
            for (int j = 0; j < 30; ++j) {
                if (k&(1<<j)) {
                    if ((v[i]&(1<<j)) == 0) {
                        l = i;
                        break;
                    }
                } else {
                    if ((v[i]&(1<<j)) == 0) {
                        if (idx[j] != -1)
                            st.erase(st.find(idx[j]));
                        idx[j] = i;
                        st.insert(idx[j]);
                    }
                }
            }
            if (st.size() == cnt && *st.begin() > l) {
                ans += *st.begin()-l;
            }
        }
        return ans;
    }
};
