class Solution {
public:


    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        map<long long,long long>mp1;
        for(int i = nums.size() - 1; i >= 0; i--) {
            map<long long, long long >mp2;
            // cout <<"mp1 "<<endl;
            // for(auto it:mp1) {
                // cout << it.first <<" " << it.second <<endl;
            // }
            ans += (nums[i] == k);
            mp2[nums[i]] = 1;
            for(auto it:mp1) {
                // cout << "qw e " << (it.first & nums[i]) << endl;
                mp2[(it.first & nums[i])] += it.second;
                if((it.first & nums[i] ) == k)
                ans += it.second;
            }
            // cout <<"in "<<i<<endl;
            // for(auto it:mp2) cout << it.first <<" "<< it.second << endl;
            // if(mp2.find(k) != mp2.end())
            // ans += mp2[k];
            mp1 = mp2;
            // cout << "i "<< i << " "<<ans <<" "<<mp2[k] << endl;
        }
        return ans;
    }
};
