#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

      map<ll,ll>mpp,tmp;
      ll res = 0;

      for(auto &it : nums)
      {
        if(it==k) res++;

        tmp[it] = 1;

        for(auto &i : mpp)
        {
          if((i.first&it) == k)
          {
            res += i.second;
             tmp[i.first&it] += i.second;

          }
          else
          {
             tmp[i.first&it] += i.second;
          }

        }

        mpp = tmp;
        tmp.clear();
      }   
       return res;
    }
};
