class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
         map<long long int,long long int>m;


         int n=nums.size();

   long long int ans=0;
         for(int i=0;i<n;i++){
              map<long long int,long long int>x;
              for(auto it:m){
                long long int el=it.first & nums[i];
                x[el] +=it.second;
                if(el==k)ans = ans + it.second;

              }
               if(nums[i]==k) ans++;
              x[nums[i]]++;

               m=x;
         }

         return ans;
    }
};
