class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        old = Counter()
        ans = 0
        for num in nums:
            new = Counter({num: 1})
            for x, c in old.items():
                new[x & num] += c
            old = new
            ans += old[k]

        return ans
