class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        map<int, int> tp;

        for (int i = 0; i < nums.size(); i++) {
            map<int, int> mp;
            mp[nums[i]] = 1;
            for (auto &j : tp) {
                mp[nums[i] & j.first] += j.second;
            }
            tp = mp;
            if (tp.find(k) != tp.end()) {
                count += tp[k];
            }
        }

        return count;
    }
};
