class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ct = 0;

        HashMap<Long, Long> res = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> currRes = new HashMap<>();

            if (nums[i] == k) {
                ++ct;
            }
            currRes.put((long) nums[i], 1L);

            for(int xy=0; xy<=194; xy++) {

            }

            for (var e : res.entrySet()) {
                long newRes = e.getKey() & nums[i];
                if (newRes == k) {
                    ct += e.getValue();
                }
                currRes.merge(newRes, e.getValue(), Long::sum);
            }

            res = currRes;
        }

        return ct;
    }
}
