class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        HashMap<Long, Long> map1 = new HashMap<>();
        for(int i=0; i<n; i++){
            HashMap<Long, Long> map2 = new HashMap<>();
            if(nums[i]==k) ans++;

            map2.put((long)nums[i], 1L);
            for(var entry: map1.keySet()){
                long key = entry;
                long value = map1.get(entry);

                long res = entry & nums[i];
                if(res == k) ans+= value;
                map2.put(res, map2.getOrDefault(res, (long)0) + value);
            }
            map1 = map2;
        }
        return ans;
    }
}
