B = 30
def _get(v):
    out = [0] * B
    for i in range(B):
        if v&(1<<i):
            out[i] = 1
    return out
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        tar = _get(k)
        pres = [[-1] * B]
        out = 0
        for i,v in enumerate(nums):
            barr = _get(v)
            temp = [-1] * B
            for j in range(B):
                if barr[j] == 0:
                    temp[j] = i
                else:
                    temp[j] = pres[-1][j]
            pres.append(temp)
            x,y = 0,i
            for j in range(B):
                if tar[j]:
                    x = max(x,pres[-1][j]+1)
                else:
                    y = min(y,pres[-1][j])
            out += max(0,y-x+1)
        return out
