#define ll long long int
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n=nums.size(),ans=0;
        map<ll,ll> m1,m2;
        for(ll i=0;i<n;i++){
            if(nums[i]==k) ans++;
            m2[nums[i]]=1;
            for(auto it=m1.begin();it!=m1.end();it++){
                if(((*it).first&nums[i])==k){
                    ans+=(*it).second;
                }
                m2[(*it).first&nums[i]]+=(*it).second;
            }
            m1=m2,m2.clear();
        }
        return ans;
    }
};
