#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        vector<vector<ll>>v(n,vector<ll>(31));
        for(ll i=30;i>=0;i--){
           ll prev =-1;
           for(int j=0;j<n;j++){
            if(((1ll<<i)&nums[j])){
                v[j][i] =prev;
            }
            else{
                prev =j;
                v[j][i]=j;
            }
           }
        }
        // for(int i=0;i<n;i++){
        //     cout<<v[i][1]<<" ";
        // }
        // cout<<endl;

        ll ans =0;
        for(ll i=0;i<n;i++){
            ll mini =-1;
            ll maxi =i;
           for(ll j=30;j>=0;j--){
            bool flag =0;
            if((1ll<<j)&k){
                flag  =1;
            }
            if(flag){
            mini = max(mini,v[i][j]);
            }
            else
            maxi = min(maxi,v[i][j]);
           }
        //    cout<<i<<" "<<mini<<" "<<maxi<<endl;
            if(mini<maxi){
                ans+=(maxi-mini);
            }
        }
        return ans;



    }
};
