class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        int bitcnt[100005][32];
        int g=0;
        for(auto i:nums){
            for(int j=0;j<31;j++){
                bitcnt[g+1][j]=bitcnt[g][j];
                if((i>>j)&1)bitcnt[g+1][j]++;
            }
            g++;
        }
        long long sub=0;
        for(int i=0;i<n;i++){
            int l=i,r=n-1,ans=-1;
            while(l<=r){
                int m=(l+r)/2;
                int sum=0;
                for(int j=0;j<31;j++){
                    if(bitcnt[m+1][j]-bitcnt[i][j]==m-i+1)sum+=(1<<j);
                }
                if(sum==k){
                    ans=m;
                    r=m-1;
                }
                else if(sum<k)r=m-1;
                else l=m+1;
            }
          //  cout<<ans<<" ";
            if(ans==-1)continue;
             l=i,r=n-1;
            int ans2=-1;
            while(l<=r){
                int m=(l+r)/2;
                int sum=0;
                for(int j=0;j<31;j++){
                    if(bitcnt[m+1][j]-bitcnt[i][j]==m-i+1)sum+=(1<<j);
                }
                if(sum==k){
                    ans2=m;
                    l=m+1;
                }
                else if(sum>k)l=m+1;
                else r=m-1;
            }
           // cout<<ans2<<"\n";
            sub+=ans2-ans+1;
        }
        return sub;
    }
};
