class Solution {
   public:
    long long countSubarrays(vector<int>& nums, int r) {
        unordered_map<int, long long> memo;
        unordered_map<int, long long> meme;
        unordered_map<int, long long> momo;
        int p;
        for (auto x : nums) {
            momo.clear();
            for (auto [k, v] : meme) {
                p = k & x;
                momo[p] += v;
            }
            momo[x]++;
            meme = momo;
            for (auto [k, v] : momo)
                memo[k] += v;
        }
        return memo[r];
    }
};
