class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {


        long long ans = 0;

        int n = nums.size();

        map<int,int>ma1;

        for(int i=0;i<n;i++){

            map<int,int>ma;
            if(nums[i] == k) ans++;

            ma[nums[i]]++;

            for(auto it : ma1){

                int t = it.first & nums[i];

                if(t == k){
                    ans += it.second;
                }
                ma[t]+=it.second;
            }

            ma1 = ma;

        }
        return ans;
    }
};
