class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long n = a.size();
        map<long long , long long> tot;
        map<long long, long long> cur;
        cur[a[0]]++;
        tot[a[0]]++;
        for(int i = 1; i < n; i++){
            map<long long, long long> nw;
            nw[a[i]]++;
            for(auto x: cur){
                int nw_nd = x.first & a[i];
                nw[nw_nd] += x.second;
            }

            for(auto x: nw){
                tot[x.first] += x.second;
            }
            cur = nw;
        }
        long long ans = 0;
        ans = tot[k];
        return ans;
    }
};
