class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int len = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> pre;

        for (int i = 0; i < len; ++i) {
            unordered_map<long long, long long> cur; 

            if (nums[i] == k) {
                ++cnt;
            }
            cur[nums[i]] = 1;

            for (auto& [val, frq] : pre) {
                long long res = val & nums[i];
                if (res == k) {
                    cnt += frq;
                }
                cur[res] += frq;
            }

            pre = cur;
        }

        return cnt;
    }
};
