#define ll long long 


class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll count=0;
        int n=nums.size();

        unordered_map< ll , ll >mp1;
        for(int i=0;i<n;i++){
              unordered_map<ll, ll  >mp2;
            if(nums[i]==k)count++;
            mp2[nums[i]]=1;
            for(auto x:mp1){

                ll A=x.first&nums[i];
                if(A==k)count+=x.second;

            mp2[A]+=x.second;
        }
            mp1=mp2;}
        return count;

    }
};
