class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long ans = 0;

        map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            map<long long, long long> curr; 

            if (arr[i] == k) {
                ans += 1;
            }

            curr[arr[i]] = 1;

            for (auto& it : prev) {
                int xx = it.first;
                int ss = it.second;
                long long val = xx;
                val &= arr[i];
                if (val == k) {
                    ans += ss;
                }
                curr[val] += ss;
            }

            prev = curr;
            arr[i] -= k;
        }

        return ans;
    }
};
