class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<map<int,int>> mp(n);
        mp[0][nums[0]]++;
        for(int i=1;i<n;++i){
            for(auto p: mp[i-1]){
                mp[i][p.first&nums[i]] += p.second;
            }
            mp[i][nums[i]]++;
        }
        long long ans = 0;
        for(int i=0;i<n;++i){
            for(auto p: mp[i]){
                if(p.first==k){
                    ans += p.second;
                }
            }
        }
        return ans;
    }
};
