class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> tillnow;
        long long ans=0;
        for(int i=0;i<n;i++){
            map<int,int> now;
            for(auto x:tillnow){
                int a=(x.first&nums[i]);
                if(a>=k){
                    // cout<<i<<" "<<a<<"\n";
                    now[a]+=x.second;
                }
            }
            tillnow=now;
            if(nums[i]>=k) tillnow[nums[i]]++;
            // cout<<tillnow[k]<<"\n";
            ans+=tillnow[k];
        }
        return ans;

    }
};
