class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        pre = defaultdict(int)
        ans = 0
        for num in nums:
            cur = defaultdict(int)
            for p in pre:
                cur[p&num] += pre[p]
            cur[num] += 1
            ans += cur[k]
            pre = cur
        return ans
