class Solution:
    def countSubarrays(self, a: List[int], k: int) -> int:
        n = len(a)
        b = [(i & k) == k for i in a]
        print(b)
        c = []
        for i in range(n):
            if not b[i]:
                continue
            if i == 0 or not b[i - 1]:
                c.append([a[i]])
            else:
                c[-1].append(a[i])
        print(c)
        d = [i for i in range(30) if (k & (1 << i)) == 0]
        print(d)
        ans = 0
        for v in c:
            u = 0
            t = [0] * 30
            g = len(d)
            for i in range(len(v)):
                while u < len(v) and g > 0:
                    for j in d:
                        if (v[u] & (1 << j)) == 0:
                            t[j] += 1
                            if t[j] == 1:
                                g -= 1
                    u += 1
                # print(v, i, u, t, g)
                if g > 0:
                    break
                ans += len(v) - u + 1
                for j in d:
                    if (v[i] & (1 << j)) == 0:
                        t[j] -= 1
                    if t[j] == 0:
                        g += 1
        return ans
