class Solution {
public:

    long long funct(vector<int>& nums, int k) {
        long long count = 0;
        long long n = nums.size();
        unordered_map<long long, long long> mp;

        for (int start = 0; start < n; ++start) {
            unordered_map<long long, long long> val;
            for (auto& [key, value] : mp) {
                int fin = key & nums[start];
                val[fin] += value;
            }
            val[nums[start]]++;

            for (auto& [key, value] : val) {
                if (key == k) {
                    count += value;
                }
            }

            mp = val;
        }

        return count;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        return funct(nums, k);
    }
};
