class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<pair<int,int>> dp;
        long long ans=0;
        for(int i=0;i<n;i++){
            vector<pair<int,int>> temp;
            for(int j=0;j<dp.size();j++){
                temp.push_back({dp[j].first&nums[i],dp[j].second});
            }
            temp.push_back({nums[i],1});
            sort(temp.begin(),temp.end());
            while(dp.size()) dp.pop_back();
            for(int j=0;j<temp.size();j++){
                if(temp[j].first==k) ans=ans+temp[j].second;
                if(dp.size() && dp.back().first==temp[j].first) dp.back().second+=temp[j].second;
                else dp.push_back(temp[j]);
            }
        }
        return ans;
    }
};
