class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> prevResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currResults;

            // Check if current number equals k
            if (nums[i] == k) {
                ++count;
            }

            // Initialize current result for current number
            currResults[nums[i]] += 1;

            // Traverse previous results
            for (auto& [val, freq] : prevResults) {
                long long newAndResult = val & nums[i];

                // Check if new AND result equals k
                if (newAndResult == k) {
                    count += freq;
                }

                // Add current frequency to current result
                currResults[newAndResult] += freq;
            }

            // Update previous results with current results
            prevResults = currResults;
        }

        return count;
    }
};
