class Solution {
public:
    int n;
    unordered_map<long long, long long>prev_result;

    long long countSubarrays(vector<int>& nums, int k) {
         n = nums.size();
        long long cnt = 0;

        for (int i = 0; i < n; i++)
        {
            unordered_map<long long, long long>curr; 
            if (nums[i] == k) cnt++;
            curr[nums[i]] = 1;

            for(auto it : prev_result)
            {
                long long value = it.first;
                long long frequency = it.second;
                if ((value & nums[i]) == k)
                {
                    cnt += frequency;
                }
                curr[(value & nums[i])] += frequency;
            }

            prev_result = curr;
        }

        return cnt;
    }
};
