public class Solution {
    public long CountSubarrays(int[] nums, int k)
    {
        var dp = new Dictionary<(int, int), long>();

        long result = 0;

        for (int i = 0; i < nums.Length; i++)
        {
            result += Solve(i + 1, nums[i]);
        }

        return result;

        long Solve(int index, int sum)
        {
            if (sum < k)
            {
                return 0;
            }

            if (index == nums.Length)
            {
                return sum == k ? 1 : 0;
            }

            if (dp.ContainsKey((index, sum)))
            {
                return dp[(index, sum)];
            }

            dp[(index, sum)] = Solve(index + 1, sum & nums[index]);

            if (sum == k)
            {
                dp[(index, sum)]++;
            }

            return dp[(index, sum)];
        }
    }
}
