class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           long long n = nums.size();
    long long c = 0;
    map<long long, long long> mp;
    // mp[0] = 1; 

     for (long long i : nums) {
            map<long long, long long> mp1;
       for (auto [x, freq] : mp) {
            long long new_x = x & i;
            mp1[new_x] += freq;
            if (new_x == k) {
                c += freq;
            }
        }
        mp1[i] += 1;
        if (i == k) {
            c += 1;
        }
        mp = mp1;
    }

    return c;
    }
};
