class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        cnt = 0
        mp = defaultdict(int)
        for n in nums:
            mp2 = defaultdict(int)
            if n == k:
                cnt += 1
            mp2[n] += 1
            for key, val in mp.items():
                new_key = key & n
                if new_key == k:
                    cnt += val
                mp2[new_key] += val
            mp = mp2
        return cnt
