class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        dic = {}
        ans = 0
        for v in nums:
            tmp = {}
            for ki in dic.keys():
                if ki&v in tmp:
                    tmp[ki&v]+=dic[ki]
                else:
                    tmp[ki&v]=dic[ki]
            if v in tmp:tmp[v]+=1
            else:tmp[v]=1
            ans+=tmp[k] if k in tmp else 0
            dic = tmp
        return ans
