class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        #define ll long long
        map<int,int> fre[2];
        int turn =0;
        long long ans =0;
        int n = nums.size();
        for(int i=0;i<n;i++){
            for(auto ele: fre[turn^1]){
                int x = ele.first & nums[i];
                fre[turn][x]+= ele.second;
            }
            fre[turn][nums[i]]++;
            ans += fre[turn][k];
            fre[turn^1].clear();
            turn ^= 1;
        }
        return ans;
    }
};
