class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;

        unordered_map<long long,long long>result;
        for(int i=0 ; i<n ; i++)
        {
            unordered_map<long long,long long>current;
            if(nums[i]==k)
            {
                count++;
            }
            current[nums[i]]=1;
            for(auto &[val,freq]:result)
            {
                long long newand=val & nums[i];
                if(newand==k)
                {
                    count=count+freq;
                }
                current[newand]+=freq;
            }
            result=current;
        }
        return count;


    }
};
