class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0;

        // k 的位为 1 的位置，不能有 0 与进来
        //     位为 0 的位置，必须要一个 0 与进来

        std::unordered_map<long long, long long> prev;

        for (const int num : nums) {
            std::unordered_map<long long, long long> cur;

            if (num == k) {
                ++res;
            }

            cur[num] = 1;

            for (const auto [key, value] : prev) {
                long long temp = key & num;

                if (temp == k) {
                    res += value;
                }

                cur[temp] += value;
            }

            prev = std::move(cur);
        }

        return res;
    }
};
