class Solution {
    public long countSubarrays(int[] a, int k) {
        long ct = 0;
        int n=a.length;
        if(a[0] == k) ++ct;
        if(n==1) return ct;
        int[] gg = new int[31];
        for(int i=0;i<31;i++){
            if(((1<<i)&a[0]) > 0) gg[i] ++;
        }

        for(int i=1;i<n;i++){

            for(int j=0;j<31;j++){
                if(((1<<j)&a[i]) == ((1<<j)&a[i-1])) gg[j]++;
                else gg[j]=1;
            }

            int minzero = n+1,maxone = 0;
            for(int j=0;j<31;j++){
                if(((1<<j)&k) == 0){
                    // unset in k

                    if(((1<<j)&a[i]) == 0){
                        // also zero in a[i];
                        minzero = Math.min(minzero , i);
                    }else {
                        if( i-gg[j] >=0 ) minzero = Math.min(minzero , i-gg[j]);
                        else minzero = -1;
                    }
                }else {

                    if(((1<<j)&a[i]) == 0){
                        // also zero in a[i];
                        maxone = i+1;
                    }else {
                        maxone = Math.max(maxone , i-gg[j]+1);
                    }
                }
            }

            if(maxone <= minzero) ct += minzero - maxone +1 ; 
        }
        return ct;
    }
}
